// Arrows
const right_svg = `<svg  width="100%" height="100%" fill="#ffffff" viewBox="0 -960 960 960">

</svg>`;

import { tech_stack_box } from "../main.js";

// Label position: set to 'above' or 'below' to control where stack names are placed
const LABEL_POSITION = 'below'; // change to 'above' if you prefer labels above icons

export default () => {
  // direct Add Svg using element
  let selectMenu = document.querySelector("[menuBar]");
  selectMenu.innerHTML = menuBar_one_svg;
  let selectRightArrow = document.querySelectorAll("[project_guid_right_arrow]");
  selectRightArrow.forEach((e) => {
    e.innerHTML = right_svg;
  });

  // Tech Stack
  class TechStack {
    constructor(svg, name) {
      this.svg = svg;
      this.name = name || '';
    }
    execute() {
      let svg = this.svg;

      let mainDiv = document.createElement("div");
      mainDiv.className =
        "tilt_elm card_skill relative flex-grow-[1] size-[200px] max-lg:size-[150px] bg-black_two_clr flex justify-center items-center rounded-lg cursor-pointer";

      let innerDiv = document.createElement("div");
      innerDiv.className = "absolute left-0 z-20 top-0 h-full w-full";

      let innerSpan = document.createElement("span");
      innerSpan.className = "absolute z-10";
      innerSpan.innerHTML = svg;

      mainDiv.appendChild(innerDiv);
      mainDiv.appendChild(innerSpan);

      // Wrap main element and label to avoid overlap; labels placed above or below based on LABEL_POSITION
      let wrapperDiv = document.createElement("div");
      wrapperDiv.className = "flex flex-col items-center gap-2";

      // Label for the stack name (above or below the icon)
      let labelDiv = document.createElement("div");
      labelDiv.className = "w-full flex justify-center";
      let labelSpan = document.createElement("span");
      labelSpan.className = "text-xs text-white/80 text-center";
      labelSpan.innerText = this.name || "";
      // hide the label when no name provided
      labelDiv.style.display = this.name ? 'flex' : 'none';
      labelDiv.appendChild(labelSpan);

      if (typeof LABEL_POSITION !== 'undefined' && LABEL_POSITION === 'above') {
        wrapperDiv.appendChild(labelDiv);
        wrapperDiv.appendChild(mainDiv);
      } else {
        wrapperDiv.appendChild(mainDiv);
        wrapperDiv.appendChild(labelDiv);
      }

      tech_stack_box.appendChild(wrapperDiv);

      // Mouse Move Animation
      function mouseMoveAnimi() {
        innerDiv.addEventListener("mousemove", (e) => {
          let x = e.offsetX;
          let y = e.offsetY;
          innerSpan.style.cssText = `
                    scale: 1.5;
                    display: inline-block;
                    position: absolute; 
                    left: ${x - innerSpan.clientWidth / 2}px;
                    top: ${y - innerSpan.clientHeight / 2}px;
                    `;
        });
        innerDiv.addEventListener("mouseout", () => {
          innerSpan.style.cssText = `
                    transition: all 0.25s ease-out;
                    left: 50%;
                    top: 50%;
                    transform: translateX(-50%) translateY(-50%);
                    `;
        });
      }
      mouseMoveAnimi();
    }
  }

  // Languages & Frontend
  new TechStack(html_svg, 'HTML').execute();
  new TechStack(css_svg, 'CSS').execute();
  new TechStack(js_svg, 'JavaScript').execute();
  new TechStack(python_svg, 'Python').execute();

  // Frameworks & Tools
  new TechStack(bootstrap_svg, 'Bootstrap').execute();
  new TechStack(git_svg, 'Git').execute();
  new TechStack(postman_svg, 'Postman').execute();

  // Databases
  new TechStack(postgresql_svg, 'PostgreSQL').execute();
  new TechStack(mongo_svg, 'MongoDB').execute();

  // API & Backend
  new TechStack(fastapi_svg, 'FastAPI').execute();

  // Data & AI
  new TechStack(dialogflow_svg, 'Dialogflow').execute();
  new TechStack(numpy_svg, 'NumPy').execute();
  new TechStack(pandas_svg, 'Pandas').execute();
  new TechStack(matplotlib_svg, 'Matplotlib').execute();
  new TechStack(ai_svg, 'AI').execute();
  new TechStack(chatbot_svg, 'Chatbot').execute();
  new TechStack(automation_svg, 'Automation').execute();
  new TechStack(nlp_svg, 'NLP').execute();
  new TechStack(pytorch_svg, 'PyTorch').execute();
  new TechStack(n8n_svg, 'n8n').execute();

  // Real-time
  new TechStack(socketio_svg, 'Socket.IO').execute();
};

